SELECT DISTINCT a.id ,  sch.schema_name
FROM  arc_db_schema sch, arc_services_info si,  sys_tbllst a
WHERE a.ID = si.id_lsttbl AND si.id_db_schema = sch.ID and a.ID ={0}

SELECT  a.table_name
FROM   sys_tbllst a, SYS_TBLLNK b
WHERE  b.ID_DSTTBL=a.id and a.DEFINE_ALIAS like '%ARH%' and b.ID_LSTTBL={0}

SELECT  a.ID, a.ID_GINFO, a.IS_WRITEON 
FROM  ARC_SUBSYST_PROFILE a
WHERE {0}=a.ID_TBLLST AND a.ID_GINFO={1}

SELECT ID,ID_GINFO,ID_TBLLST,DEPTH,IS_ACTIVE,TABLE_NAME,DT_START,DT_END
   , '0' AS CNT, FROM_DT1970(DT_START) AS DATESTART,  FROM_DT1970(DT_END) AS DATEEND 
FROM  {0}
WHERE ID_GINFO= {1} AND ID_TBLLST= {2}
ORDER BY ID

SELECT ID,ID_GINFO,ID_TBLLST,DEPTH,IS_ACTIVE,TABLE_NAME,DT_START,DT_END
   , '0' AS CNT, '0' AS DATESTART,  '0' AS DATEEND
FROM  {0}
WHERE ID_GINFO= {1} AND ID_TBLLST= {2}
ORDER BY ID

CREATE [OR REPLACE] [PUBLIC] SYNONYM [schema .] synonym_name  FOR [schema .] object_name [@ dblink];

DROP [PUBLIC] SYNONYM [schema .] synonym_name [force];

